ndif; ?>						</a>					<?php else :?>						<a><?php echo $item->title; ?>						<?php if ($item->displayHits) :?>							<span class="mod-articles-category-hits">								(<?php echo $item->displayHits; ?>) 							</span>						<?php endif; ?></a>					<?php endif; ?>				</li>				<?php endif; ?>							<?php endforeach; ?>		</ul>	</div>	</div>